sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var affectedUsersList = [];
    var ci = current.getValue('cmdb_ci');

    //Clear existing user list
    if (current.getValue('cmdb_ci') != previous.getValue('cmdb_ci')) {
        var m2m_impact_user = new GlideRecord('x_pqt_impact_manag_m2m_x_pqt_impac_sys_user');
        m2m_impact_user.addQuery('u_impact', current.getValue('sys_id'));
        m2m_impact_user.query();
        m2m_impact_user.deleteMultiple();

// Clear existing CI relationship
        var m2m_impact_affected_ci = new GlideRecord('x_pqt_impact_manag_m2m_x_pqt_impac_cmdb_ci');
        m2m_impact_affected_ci.addQuery('u_impact', current.getValue('sys_id'));
		m2m_impact_affected_ci.query();
        if(m2m_impact_affected_ci.next()){
            m2m_impact_affected_ci.deleteRecord();
        }
    }
    //Populate new user list
    if (ci != null) {
        //Retrieve users associated to CI
        var installBaseItem = new GlideRecord('sn_install_base_item');
        installBaseItem.addQuery('configuration_item', current.getValue('cmdb_ci'));
        installBaseItem.addQuery('state', 'in_use');
        installBaseItem.addEncodedQuery('contactISNOTEMPTY^ORaccountISNOTEMPTY');
        installBaseItem.query();
        while (installBaseItem.next()) {
            if (installBaseItem.getValue('contact') != '') {
                affectedUsersList.push(installBaseItem.getValue('contact'));
            } else if (installBaseItem.getValue('account') != '') {
                var primary_contact = installBaseItem.getElement('account').getValue('primary_contact');
                affectedUsersList.push(primary_contact);
            }
        }
        //Insert users into Affected users List
        affectedUsersList.forEach(function(user) {
			// var accountDetails = new GlideRecord("sys_user");
			// accountDetails.addQuery('sys_id',"user");
			// accountDetails.query();

			// if(accountDetails.next()){
			// 	var accountValue=accountDetails.getValue('company').toString();
			// }
            var m2m_impact_user = new GlideRecord('x_pqt_impact_manag_m2m_x_pqt_impac_sys_user');
            m2m_impact_user.initialize();
            m2m_impact_user.setValue('u_impact', current.getValue('sys_id'));
            m2m_impact_user.setValue('u_user', user);
			//m2m_impact_user.setValue('u_account', accountValue);
            m2m_impact_user.insert();
        });
        var m2m_impact_ci = new GlideRecord('x_pqt_impact_manag_m2m_x_pqt_impac_cmdb_ci');
        m2m_impact_ci.setValue('u_impact', current.getValue('sys_id'));
        m2m_impact_ci.setValue('cmdb_ci', current.cmdb_ci);
        m2m_impact_ci.insert();
    }
})(current, previous);
