sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var impOutages = current.getValue('outage');

    //Clear existing outage list
    if (current.getValue('outage') != previous.getValue('outage')) {
        var m2m_impact_parent = new GlideRecord('x_pqt_impact_manag_m2m_x_pqt_impac_cmdb_ci_outa');
        m2m_impact_parent.addQuery('u_impact', current.getValue('sys_id'));
        m2m_impact_parent.query();
        m2m_impact_parent.deleteMultiple();
    }
    if (impOutages != null) {
        var m2m_impact_outage = new GlideRecord('x_pqt_impact_manag_m2m_x_pqt_impac_cmdb_ci_outa');
        //m2m_impact_outage.initialize();
        m2m_impact_outage.setValue('cmdb_ci_outage', current.getValue('outage'));
        m2m_impact_outage.setValue('u_impact', current.getValue('sys_id'));
        m2m_impact_outage.insert();
    }


})(current, previous);
