sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var usa_display = 'undefine';
    var europe_display = 'undefine';
    var singapore_display = 'undefine';
	var timezone = new global.TimeZone();
    if (current.start_time) {

        europe_display = timezone.getTimeZone(current.getDisplayValue('start_time'), "Europe/London");

        var usa_value = timezone.getTimeZone(current.getDisplayValue('start_time'),"US/Eastern");
        // var usaTimezone_parts = usa_value.split(' ');
        // var usaTimezone_date_parts = usaTimezone_parts[0].split('-'); // WARNING - assumes system date format is dd-mm-yyyy
        // usa_display = usaTimezone_date_parts[1] + '/' + usaTimezone_date_parts[0] + '/' + usaTimezone_date_parts[2] + ' ' + usaTimezone_parts[1];
        // if (usaTimezone_parts[2]) {
        //     usa_display += ' ' + usaTimezone_parts[2];
        // }


        singapore_display = timezone.getTimeZone(current.getDisplayValue('start_time'), "Singapore");
    }
    template.print("("+usa_value +")" + ' (' + europe_display + ')' + ' (' + singapore_display + ')');

})(current, template, email, email_action, event);
