sysevent_in_email_action.script:
(function runAction( /*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {
	var account_sysid;
    var contact_info = new GlideRecord('customer_contact');
	contact_info.addQuery('email', email.origemail);
	contact_info.query();
	if(contact_info.next()){
		account_sysid = contact_info.account.toString();
	}
	var contact = new GlideRecord('sys_user');
	contact.addQuery('email',email.origemail);
	gs.info("Email Test:" + email.origemail);
	contact.query();
	contact.next();

	var impact = current.getUniqueValue();
	var caseGr = new GlideRecord('sn_customerservice_case');
	caseGr.addQuery('u_impact',impact);
	caseGr.addQuery('account', account_sysid);
	caseGr.addEncodedQuery('stateNOT IN3,7');
	caseGr.query();

	if(!caseGr.next()){
		caseGr.initialize();
		caseGr.short_description=email.subject;
		caseGr.description = email.body_text;
		caseGr.cmdb_ci = current.cmdb_ci;
		caseGr.u_impact=impact;
		caseGr.contact = contact.getUniqueValue();
		caseGr.insert();
	}else{
		caseGr.comments = email.body_text;
		caseGr.update();
	}

})(current, event, email, logger, classifier);
